import { useStore } from '@/store';

export const validateApiKeyWithModel = async (apiKey, modelName = "text-davinci-003") => {
  try {
    const response = await fetch(`https://api.openai.com/v1/engines/${modelName}/completions`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${apiKey}`,
      },
      body: JSON.stringify({
        prompt: "测试 API Key...",
        max_tokens: 5,
      }),
    });

    return response.status === 200;
  } catch (error) {
    console.error(error);
    return false;
  }
};

export const callGPT = async (apiKey, prompt = "test", model = "gpt-4", maxTokens = 1) => {
  try {
    const response = await fetch(`https://api.openai.com/v1/chat/completions`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${apiKey}`,
      },
      body: JSON.stringify({
        model: model, // 添加模型参数
        messages: [
          { role: "user", content: prompt },
        ],
        max_tokens: maxTokens,
      }),
    });

    if (response.status === 200) {
      return true;
    } else {
      throw new Error(`Error: ${response.status} ${response.statusText}`);
    }
  } catch (error) {
    console.error(error);
    return false;
  }
};
