import { makeAutoObservable } from 'mobx';
import { history, setToken, getToken, removeToken } from '@/utils';

class ApiStore {
  apiKey = getToken() || '';
  selectedModel = localStorage.getItem('selectedModel') || 'gpt-3.5-turbo';

  constructor() {
    makeAutoObservable(this);
  }

  setApiKey = (apiKey) => {
    console.log(apiKey);
    this.apiKey = apiKey;
    setToken(apiKey);
  };

  getApiKey = () => {
    return this.apiKey;
  };

  removeApiKey = () => {
    this.apiKey = '';
    removeToken();
    history.push('/login');
  };

  setSelectedModel = (model) => {
    this.selectedModel = model;
    localStorage.setItem('selectedModel', model);
  };

  getSelectedModel = () => {
    return this.selectedModel;
  };

  validateApiKeyWithModel = async (apiKey, modelName = 'text-davinci-003') => {
    try {
      const response = await fetch(`https://api.openai.com/v1/engines/${modelName}/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          prompt: '测试 API Key...',
          max_tokens: 5,
        }),
        
      });

      return response.status === 200;
    } catch (error) {
      console.error(error);
      return false;
    }
  };

  validateApiKeyWithGPT4 = async () => {
    try {
      const response = await fetch(`https://api.openai.com/v1/engines/gpt-4/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          prompt: '太阳系有哪些行星？',
          max_tokens: 5,
          n: 1,
          stop: null,
          temperature: 0.7,
        }),
      });

      return response.status === 200;
    } catch (error) {
      console.error(error);
      return false;
    }
  };
}

export default ApiStore;
