// audioUtils.js
import { message } from 'antd';
import { useTranslation } from 'react-i18next';

import { Configuration, OpenAIApi } from 'openai';
import FormData from 'form-data';

class CustomFormData extends FormData {
  getHeaders() {
    return {};
  }
}

const processAudioFile = async (audioFile, apiKey, createTranscription) => {
  const configuration = new Configuration({
    apiKey,
    formDataCtor: CustomFormData,
  });

  delete configuration.baseOptions.headers['User-Agent'];
  const openai = new OpenAIApi(configuration);

  if (audioFile) {
    const formData = new FormData();
    formData.append('file', audioFile, audioFile.name);

    const audioTranscription = await createTranscription(audioFile, 'whisper-1');
    return audioTranscription.data.text;
  }
};

const handleAudioUpload = async (audioList, apiStore, openai, content) => {
  let audio_text = '';
  const { t } = useTranslation();

  if (audioList.length > 0) {
    console.log(audioList[0]);
    const audioFile = audioList[0];

    audio_text = await processAudioFile(audioFile, apiStore.apiKey, openai.createTranscription);
  } else if (!content) {
    message.error(t('nothingError'));
    return;
  }

  return audio_text;
};

export { processAudioFile, handleAudioUpload };
