import React, { useState } from "react";
import "./index.scss";
import { Card, Form, Button, message, Upload, Popover } from "antd";
import { InboxOutlined } from "@ant-design/icons";
import { useStore } from "@/store";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import JSZip from "jszip";

const Home = () => {
  const [image, setImage] = useState(null);
  const [imageRef, setImageRef] = useState(null);
  const [crop, setCrop] = useState({ aspect: 1, unit: "%" });
  const [croppedImageBlob, setCroppedImageBlob] = useState(null);
  const [changedAfterSubmit, setChangedAfterSubmit] = useState(false);


  const handleSubmit = (info) => {

  };

  const onImageLoaded = (image) => {
    setImageRef(image);
  };

  const makeClientCrop = async (crop) => {
    if (imageRef && crop.width && crop.height) {
      const croppedImageBlob = await getCroppedImage(
        imageRef,
        crop,
        "newCrop.jpeg"
      );
      setCroppedImageBlob(croppedImageBlob);
    }
  };

  const getCroppedImage = (image, crop, fileName) => {
    const canvas = document.createElement("canvas");
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext("2d");

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      canvas.toBlob((blob) => {
        if (!blob) {
          console.error("Canvas is empty");
          return;
        }
        blob.name = fileName;
        resolve(blob);
      }, "image/jpeg");
    });
  };

  const checkUpload = async (file) => {
    if (!file || !file.type.startsWith('image/')) {
      message.error('请选择一个图片文件。');
      return false;
    }
  
    // 检查是否已经有图片存在并且尚未提交到后端，要求用户确认是否放弃之前的更改
    if (image !== null && !changedAfterSubmit) {
      const confirmResult = window.confirm('前一个图片尚未上传，您确定要放弃之前的编辑和更改吗？');
      if (!confirmResult) {
        return false;
      }
    }

    return true;
  };

  return (
    <div className="crop-image-container">
      <Card>
        <Form layout="vertical">
          <Form.Item label="上传图片">
            <Upload.Dragger
              accept="image/*"
              showUploadList={false}
              beforeUpload={(file) => {
                (async () => {
                  if (await checkUpload(file)) {
                    setImage([file]);
                    console.log("local upload",await checkUpload(file));
                  } else {
  
                    console.log("local upload failed", image);
                  }
                })();
                return false;
              }}
              onRemove={(file) => {
                // Remove the selected file from the audioList state
                setImage(null);
              }}

            >
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p>点击这里或者拖拽图片到此处上传</p>
            </Upload.Dragger>
          </Form.Item>

          {image && (
            <Form.Item label="裁剪图片">
              <ReactCrop crop={crop} onChange={c => setCrop(c)}>
                <img src={image} />
              </ReactCrop>
            </Form.Item>
          )}

          <Form.Item>
            <Button onClick={handleSubmit} type="primary">
              上传裁剪后的图片
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default Home;
