import { makeAutoObservable } from 'mobx';
import { history, setToken, getToken, removeToken } from '@/utils';
import * as openai from 'openai'; // 导入openai库
import { tokenCounter } from '../utils';
class ApiStore {
  apiKey = getToken() || '';
  selectedModel = localStorage.getItem('selectedModel') || 'gpt-3.5-turbo';

  constructor() {
    makeAutoObservable(this);
  }

  setApiKey = (apiKey) => {
    console.log(apiKey);
    this.apiKey = apiKey;
    setToken(apiKey);
  };

  getApiKey = () => {
    return this.apiKey;
  };

  removeApiKey = () => {
    this.apiKey = '';
    removeToken();
    history.push('/login');
  };

  setSelectedModel = (model) => {
    this.selectedModel = model;
    localStorage.setItem('selectedModel', model);
  };

  getSelectedModel = () => {
    return this.selectedModel;
  };

  processStream = async (reader, result) => {
    const { value: chunk, done: readerDone } = await reader.read();
    const decodedChunk = new TextDecoder("utf-8").decode(chunk);
    result += decodedChunk;

    if (readerDone) {
      return result;
    }

    return this.processStream(reader, result);
  };

  callEngines = async (prompt = "test", model = "text-davinci-003") => {
    const data = JSON.stringify({
      model,
      prompt,
      max_tokens: 1024,
      n: 1,
      stop: ["\n"],
      temperature: 0.5,
      stream: true,
    });

    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: data,
    };

    const response = await fetch(
      "https://api.openai.com/v1/engines/davinci-codex/completions",
      requestOptions
    );
    
    if (response.status === 200) {
      const reader = response.body.getReader();
      let result = "";

      const finalResult = await this.processStream(reader, result);

      console.log('finalResult',finalResult);
      return finalResult;
    } else {
      throw new Error(`API request failed with status code ${response.status}`);
    }
  };
  
  

  callGPT = async (prompt = 'test', model = "gpt-4") => {
    try {
      let maxTokens;
      if (prompt === 'test') {
        maxTokens = 5;
      } else if (model === "gpt-4") {
        maxTokens = 7700 - tokenCounter(prompt);
      } else {
        maxTokens = 3700 - tokenCounter(prompt);
      }
  
      const response = await fetch(`https://api.openai.com/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: model,
          messages: [{ role: "user", content: prompt }],
          max_tokens: maxTokens,
          temperature: 0.5,
          stream: true
        }),
      });
      console.log(response);
      if (response.status === 200) {
        const responseData = await response.json();
        console.log(responseData);
        return prompt === 'test' ? true : responseData.choices[0].message.content;
      } else {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error(error);
      return false;
    }
  };
  

  callModel = async (prompt , model ) => {
    if (model === 'gpt-3.5-turbo' || model === 'gpt-4') {
      return await this.callGPT(prompt, model);
    } else {
      return await this.callEngines(prompt, model);
    }
  };

  
}

export default ApiStore;
