import { makeAutoObservable } from 'mobx';
import { history, setToken, getToken, removeToken } from '@/utils';
import openai from 'openai'; // 导入openai库

class ApiStore {
  apiKey = getToken() || '';
  selectedModel = localStorage.getItem('selectedModel') || 'gpt-3.5-turbo';

  constructor() {
    makeAutoObservable(this);
  }

  setApiKey = (apiKey) => {
    console.log(apiKey);
    this.apiKey = apiKey;
    setToken(apiKey);
  };

  getApiKey = () => {
    return this.apiKey;
  };

  removeApiKey = () => {
    this.apiKey = '';
    removeToken();
    history.push('/login');
  };

  setSelectedModel = (model) => {
    this.selectedModel = model;
    localStorage.setItem('selectedModel', model);
  };

  getSelectedModel = () => {
    return this.selectedModel;
  };

  validateApiKeyWithModel = async ( modelName = 'text-davinci-003') => {
    try {
      const response = await fetch(`https://api.openai.com/v1/engines/${modelName}/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          prompt: '测试 API Key...',
          max_tokens: 5,
        }),
        
      });

      return response.status === 200;
    } catch (error) {
      console.error(error);
      return false;
    }
  };

  validateApiKeyWithGPT4 = async () => {
    try {
      openai.apiKey = this.apiKey; // 设置API密钥

      const response = await openai.createChatCompletion({
        model: "gpt-4",
        messages: [
          { role: "user", content: "太阳系有哪些行星？" },
        ],
      });

      if (response) {
        console.log("Success! Your API key has access to GPT-4.");
        return true;
      }
    } catch (error) {
      console.error(error);

      if (error.message.includes("Model not found")) {
        console.log("Your API key does not have access to GPT-4.");
      } else {
        console.log("An unexpected error occurred.");
      }
      return false;
    }
  };
}

export default ApiStore;
