import React, { useState } from 'react';
import {
  Card,
  Breadcrumb,
  Form,
  Input,
  Upload,
  Button,
  message,
  Typography,
  Progress,
  Space,
  Checkbox,
  Row,
} from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import { v4 as uuidv4 } from 'uuid';
import { useStore } from '@/store';
import FormData from 'form-data';
import './index.scss';
import { useTranslation } from 'react-i18next';

const { Configuration, OpenAIApi } = require('openai');
const { Title, Paragraph } = Typography;

class CustomFormData extends FormData {
  getHeaders() {
    return {};
  }
}

const NoteHelper = () => {
  const { t } = useTranslation();
  const { keyStore } = useStore();
  const [response, setResponse] = useState('');
  const [audioList, setAudioList] = useState([]);
  const [progressPercent, setProgressPercent] = useState(0);
  const [preferences, setPreferences] = useState([]);

  const estimateTokenCount = (text) => { 
    return text.split(/\s+/).length;
  };

  const callOpenAICompletionAPI = async ({ title, formType, content }) => {
    setProgressPercent(33);
    const configuration = new Configuration({
      organization: 'org-9QcLes4mblKdTtxRSBBYJTCd',
      apiKey: keyStore.apiKey,
      formDataCtor: CustomFormData,
    });
    console.log(configuration);
    delete configuration.baseOptions.headers['User-Agent'];
    const openai = new OpenAIApi(configuration);

    let audio_text = '';

    if (audioList.length > 0) {
      const audioFile = audioList[0].originFileObj;

      const formData = new FormData();
      formData.append('file', audioFile, audioFile.name);

      const audioTranscription = await openai.createTranscription(audioFile, 'whisper-1');
      audio_text = audioTranscription.data.text;
    }
    setProgressPercent(66);
    
    const preferredOutput = preferences.join(', ');
    const prompt = `Title: ${title}\nForm Type: ${formType}\nContent: ${content}\nAudio Text: ${audio_text || ''}\nPreferences: ${preferredOutput}\nPlease summarize the given information according to the preferences.`;

    const promptTokenCount = estimateTokenCount(prompt);
    const maxTokens = 3700 - promptTokenCount;
    console.log(maxTokens);
    const getResponse = await openai.createCompletion({
      model: 'text-davinci-003',
      prompt,
      temperature: 0,
      max_tokens: maxTokens,
      top_p: 1.0,
      frequency_penalty: 0.0,
      presence_penalty: 0.0,
    });

    setResponse(getResponse.data.choices[0].text);
    setProgressPercent(100);
  };

  const handleSubmit = (values) => {
    const { title, formType, content } = values;

    callOpenAICompletionAPI({ title, formType, content });
  };

  const handleUpload = async (file) => {
    const isAudio = file.type.startsWith('audio');

    if (!isAudio) {
      message.error('Please upload an audio file!');
      return Upload.LIST_IGNORE;
    }

    return true;
  };

  const onChangePreferences = (checkedValues) => {
    setPreferences(checkedValues);
  };

  return (
    <div>
      <Card
        title={
          <Breadcrumb separator=">">
            <Breadcrumb.Item>
              <a href="#">{t('noteHelper.breadcrumb.home')}</a>
            </Breadcrumb.Item>
            <Breadcrumb.Item>{t('noteHelper.breadcrumb.noteAssistant')}</Breadcrumb.Item>
          </Breadcrumb>
        }
      >
        <Space>
          <Progress className="progress_circle" type="circle" percent={progressPercent} />

          <div>
            <Title level={3}>结果显示处：</Title>
            <Paragraph>{response}</Paragraph>
          </div>
        </Space>
      </Card>
      <Card>
        <Form initialValues={{ title: '', formType: '', content: '' }} onFinish={handleSubmit}>
          {/* Replace all labels and placeholders with translated strings */}
          <Form.Item
            label={t('noteHelper.formLabel.title')}
            name="title"
            rules={[{ required: false, message: t('noteHelper.titlePlaceholder') }]}
          >
            <Input
              placeholder={t('noteHelper.titlePlaceholder')}
              style={{ width: '100%' }}
            />
          </Form.Item>
          <Form.Item
            label={t('noteHelper.formLabel.formType')}
            name="formType"
            rules={[{ required: false, message: t('noteHelper.formTypePlaceholder') }]}
          >
            <Input
              placeholder={t('noteHelper.formTypePlaceholder')}
              style={{ width: '100%' }}
            />
          </Form.Item>
          <Form.Item label={t('noteHelper.formLabel.uploadAudio')}>
            {/* ... */}
          </Form.Item>
          <Form.Item label={t('noteHelper.formLabel.outputPreferences')}>
            {/* ... */}
          </Form.Item>
          <Form.Item
            label={t('noteHelper.formLabel.textContent')}
            name="content"
            rules={[{ required: true, message: t('noteHelper.contentRulesMessage') }]}
          >
            <Input.TextArea rows={10} />
          </Form.Item>
          <Form.Item>
            <Button
              className="summary"
              size="large"
              type="primary"
              htmlType="submit"
            >
              {t('noteHelper.summarizeButton')}
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default NoteHelper;
