import React, { useState } from "react";
import "./index.scss";
import { Card, Form, Button, message, Upload, Popover } from "antd";
import { InboxOutlined } from "@ant-design/icons";
import { useStore } from "@/store";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import JSZip from "jszip";

const Home = () => {
  const [image, setImage] = useState(null);
  const [crop, setCrop] = useState({ aspect: 1, unit: "%" });
  const [croppedImageBlob, setCroppedImageBlob] = useState(null);
  const [changedAfterSubmit, setChangedAfterSubmit] = useState(false);
  const [src, setSrc] = useState(null);

  const handleSubmit = (info) => {

  };


  const selectImage = (file) => {
    setSrc(URL.createObjectURL(file));
  };


  const cropImageNow = () => {
    const canvas = document.createElement('canvas');
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext('2d');

    const pixelRatio = window.devicePixelRatio;
    canvas.width = crop.width * pixelRatio;
    canvas.height = crop.height * pixelRatio;
    ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
    ctx.imageSmoothingQuality = 'high';

    ctx.drawImage(
        image,
        crop.x * scaleX,
        crop.y * scaleY,
        crop.width * scaleX,
        crop.height * scaleY,
        0,
        0,
        crop.width,
        crop.height,
    );

    // Converting to base64
    const base64Image = canvas.toDataURL('image/jpeg');
    setImage(base64Image);
  };

  const checkUpload = async (file) => {
    if (!file || !file.type.startsWith('image/')) {
      message.error('请选择一个图片文件。');
      return false;
    }
  
    // 检查是否已经有图片存在并且尚未提交到后端，要求用户确认是否放弃之前的更改
    if (image !== null && !changedAfterSubmit) {
      const confirmResult = window.confirm('前一个图片尚未上传，您确定要放弃之前的编辑和更改吗？');
      if (!confirmResult) {
        return false;
      }
    }

    return true;
  };

  return (
    <div className="crop-image-container">
      <Card>
        <Form layout="vertical">
          {/* ... other form items */}

          <Form.Item label="裁剪图片">
            {src && (
              <ReactCrop
                crop={crop}
                onChange={(c) => setCrop(c)}
                onImageLoaded={setImage}
                src={src}
              />
            )}
            {image && (
              <>
                <img src={image} />
                <button onClick={cropImageNow}>Crop</button>
              </>
            )}
          </Form.Item>

          {/* ... other form items */}
        </Form>
      </Card>
    </div>
  );
};

export default Home;
