import logo from '@/assets/logo.png'
import './index.scss'
import { Card,Button, Checkbox, Form, Input, message   } from 'antd';
import {useStore} from '@/store'
import { useNavigate } from 'react-router-dom';
function Login() {
  //const {loginStore} = useStore()
  const { keyStore } = useStore();
  const navigate = useNavigate()
  
  const hasApiKey = !!keyStore.apiKey;
  const apiKeyLabel = hasApiKey ? "更改你的openAI key" : "你的openAI key";
  const apiKeyPlaceholder = hasApiKey ? "请输入新的openAI key" : "请输入你的openAI key";

  const handleSubmit = async (values) => {
    const apiKey = values.title;

    if (!apiKey) {
      return message.error('请输入你的openAI key');
    }

    // 验证Key是否有效
    try {
      const response = await fetch('https://api.openai.com/v1/engines/text-davinci-003/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          prompt: '测试 API Key...',
          max_tokens: 5,
        }),
      });

      if (response.status === 200) {
        message.success('API Key 验证成功！');
        // Save the validated API key in the KeyStore
        console.log(apiKey);
        keyStore.setApiKey(apiKey);
        
        console.log(keyStore.apiKey);
      } else if (response.status === 401) {
        message.error('API Key 无效，请重试。');
      } else {
        message.error('验证 API Key 时发生错误，请稍后再试。');
      }
    } catch (error) {
      message.error('发生错误，请检查网络连接并稍后再试。');
    }
  };

  return (
    <div className="login">
      <Card className="login-container">
        <img className="login-logo" src={logo} alt="" />
        <div className="App">
        <Form
          name="basic"
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          labelCol={{
            span: 2,
          }}
          
          style={{
            maxWidth: 600,
          }}
          initialValues={{
            remember: true,
          }}
          
          autoComplete="off"
        >
          <Form.Item
          initialValue="18883716551"
          name="username"
          rules ={[{
              
            required: true,
            message: 'Please input your username!',
          
        }]}>
            <Input size="large" placeholder= 'Please input your username!' />
          </Form.Item>

          <Form.Item
            name="password"
            initialValue="246810"
            rules ={[{
              
                required: true,
                message: 'Please input your password!',
              
            }]}
          >
            <Input size="large" placeholder= 'Please input your password!'  />
          </Form.Item>

          <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
              style: { display: 'flex', justifyContent: 'center' } // 新增样式
            }}
          >
            <Checkbox>Remember me</Checkbox>
          </Form.Item>

          <Form.Item
            wrapperCol={{
              
              style: { display: 'flex', justifyContent: 'center' } // 新增样式
            }}
          >
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      
        
      </div>
      </Card>
    </div>
  )
}

export default Login
