import React, { useState } from "react";
import "./index.scss";
import { Card, Form, Button, message, Upload, Popover } from "antd";
import { InboxOutlined } from "@ant-design/icons";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import JSZip from "jszip";



const Home = () => {
  const [image, setImage] = useState(null);
  const [crop, setCrop] = useState({ aspect: 1, unit: "%" });
  const [croppedImage, setCroppedImage] = useState(null);
  const [changedAfterSubmit, setChangedAfterSubmit] = useState(false);
  const [src, setSrc] = useState(null);
  const [completedCrop, setCompletedCrop] = useState(null);
  const handleSubmit = (info) => {

  };

  // 裁剪图片
  const cropImageNow = () => {
    const image_HTML = new Image();
    image_HTML.src = src;

    const canvas = document.createElement('canvas');
    console.log("src",src);
    console.log("canvas",canvas);
    console.log("image",image);
    
    const scaleX = image_HTML.naturalWidth / image_HTML.width;
    const scaleY = image_HTML.naturalHeight / image_HTML.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext('2d');

    const pixelRatio = window.devicePixelRatio;
    canvas.width = crop.width * pixelRatio;
    canvas.height = crop.height * pixelRatio;
    ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
    ctx.imageSmoothingQuality = 'high';
    
    console.log("crop",crop)
    console.log("scaleX",scaleX)
    console.log("scaleY",scaleY)
    

    console.log("image_HTML",image_HTML);
    ctx.drawImage(
      image_HTML,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height,
    );

    // Converting to base64
    const base64Image = canvas.toDataURL('image/jpeg');
    setCroppedImage(base64Image);
    console.log("base64Image",base64Image);
  };

  // 本地上传之前检查图片
  const checkLocalUpload = async (file) => {
    if (!file || !file.type.startsWith('image/')) {
      message.error('请选择一个图片文件。');
      return false;
    }
  
    // 检查是否已经有图片存在并且尚未提交到后端，要求用户确认是否放弃之前的更改
    if (image !== null && !changedAfterSubmit) {
      const confirmResult = window.confirm('前一个图片尚未上传，您确定要放弃之前的编辑和更改吗？');
      if (!confirmResult) {
        return false;
      }
    }

    return true;
  };

  // 本地上传
  const handleBeforeUpload = async (file) => {
    if (await checkLocalUpload(file)) {
      setSrc(URL.createObjectURL(file));
      setImage(file);
      console.log("local upload", await checkLocalUpload(file));
    } else {
      console.log("local upload failed", image);
    }
    return false;
  };

  return (
    <div className="crop-image-container">
      <Card
    title="Card title"
    bordered={false}
    style={{
      width: 300,
    }}
  >
        <Form layout="vertical">
          

          <Form.Item label="裁剪图片">
            <ReactCrop src={src} onImageLoaded={setImage} crop={crop}  onChange={c => setCrop(c)} onComplete={(c) => setCompletedCrop(c)} >


              <img src={src} />

            </ReactCrop>
            <img src={croppedImage} />
            <Button onClick={cropImageNow}>Crop</Button>
          </Form.Item>

          <Form.Item label="上传图片">
            <Upload.Dragger
              accept="image/*"
              showUploadList={false}
              beforeUpload={handleBeforeUpload}
              onRemove={(file) => {
                console.log("onRemove", file);
                setSrc(null);
                setImage(null);
              }}
            >
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p>点击这里或者拖拽图片到此处上传</p>
            </Upload.Dragger>
          </Form.Item>

          <Form.Item>
            <Button className = "submit-button" onClick={handleSubmit} type="primary">
              上传图片
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default Home;
