import React, { useState } from "react";
import "./index.scss";
import { Card, Form, Button, message, Upload, Popover } from "antd";
import { InboxOutlined } from "@ant-design/icons";
import { useStore } from "@/store";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import JSZip from "jszip";

const Home = () => {
  const [imageList, setImageList] = useState([]);
  const [imageRef, setImageRef] = useState(null);
  const [crop, setCrop] = useState({ aspect: 1, unit: "%" });
  const [croppedImageBlob, setCroppedImageBlob] = useState(null);

  const handleChange = (info) => {
    console.log(info);

    // 仅改变本地状态并创建对象URL
    setImageList([info.file.originFileObj]);
    setImageUrl(URL.createObjectURL(info.file.originFileObj));
  };

  const onImageLoaded = (image) => {
    setImageRef(image);
  };

  const makeClientCrop = async (crop) => {
    if (imageRef && crop.width && crop.height) {
      const croppedImageBlob = await getCroppedImage(
        imageRef,
        crop,
        "newCrop.jpeg"
      );
      setCroppedImageBlob(croppedImageBlob);
    }
  };

  const getCroppedImage = (image, crop, fileName) => {
    const canvas = document.createElement("canvas");
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext("2d");

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      canvas.toBlob((blob) => {
        if (!blob) {
          console.error("Canvas is empty");
          return;
        }
        blob.name = fileName;
        resolve(blob);
      }, "image/jpeg");
    });
  };

  const handleUpload = async () => {
    if (!croppedImageBlob) {
      message.error("请先上传并裁剪图片！");
      return;
    }

    const zip = new JSZip();
    zip.file(`${Date.now()}.jpeg`, croppedImageBlob);
    const content = await zip.generateAsync({ type: "blob" });
    // 使用 content 进行上传操作

    // 您需要根据您实际使用的 API 接口进行修改：
    // const formData = new FormData();
    // formData.append("file", content, `${Date.now()}.zip`);
    // axios.post("/upload/zip", formData).then((response) => {
    //   message.success("图片已成功上传");
    // });
  };

  return (
    <div className="crop-image-container">
      <Card>
        <Form layout="vertical">
          <Form.Item label="上传图片">
            <Upload.Dragger
              accept="image/*"
              showUploadList={false}
              beforeUpload={() => false}
              onChange={handleChange}
            >
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p>点击这里或者拖拽图片到此处上传</p>
            </Upload.Dragger>
          </Form.Item>

          {imageList.length > 0 && (
            <Form.Item label="裁剪图片">
              <ReactCrop
                src={URL.createObjectURL(imageList[0])}
                crop={crop}
                ruleOfThirds
                onImageLoaded={onImageLoaded}
                onComplete={makeClientCrop}
                onChange={(newCrop) => setCrop(newCrop)}
              />
            </Form.Item>
          )}

          <Form.Item label="预览图片">
            {imageList.length > 0 && (
              <img
                alt="Preview"
                src={
                  croppedImageBlob
                    ? URL.createObjectURL(croppedImageBlob)
                    : URL.createObjectURL(imageList[0])
                }
              />
            )}
          </Form.Item>

          <Form.Item>
            <Button onClick={handleUpload} type="primary">
              上传裁剪后的图片
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default Home;
