import { makeAutoObservable } from 'mobx';
import { history, setToken, getToken, removeToken } from '@/utils';

class ApiStore {
  apiKey = getToken() || '';
  selectedModel = localStorage.getItem('selectedModel') || 'gpt-3.5-turbo';

  constructor() {
    makeAutoObservable(this);
  }

  setApiKey = (apiKey) => {
    console.log(apiKey);
    this.apiKey = apiKey;
    setToken(apiKey);
  };

  getApiKey = () => {
    return this.apiKey;
  };

  removeApiKey = () => {
    this.apiKey = '';
    removeToken();
    history.push('/login');
  };

  setSelectedModel = (model) => {
    this.selectedModel = model;
    localStorage.setItem('selectedModel', model);
  };

  getSelectedModel = () => {
    return this.selectedModel;
  };

  validateApiKeyWithModel = async (modelName = 'gpt-4') => {
    try {
      const response = await fetch(`https://api.openai.com/v1/engines/${modelName}/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          prompt: '太阳系有哪些行星？',
          max_tokens: 5,
          n: 1,
          stop: null,
          temperature: 0.7,
        }),
      });

      return response.status === 200;
    } catch (error) {
      console.error(error);
      return false;
    }
  };
}

export default ApiStore;
