import React, { useState } from 'react';
import './index.scss';
import { Bar } from '@/components/Bar';
import { Card, Breadcrumb, Form, Input, Upload, Button, message } from 'antd';
import { useStore } from '@/store';
import { useTranslation } from 'react-i18next';

const Home = () => {
  const [form] = Form.useForm();
  const { keyStore } = useStore();
  const { t, ready } = useTranslation();

  // 判断是否已经存有 key
  const hasApiKey = !!keyStore.apiKey;
  const apiKeyLabel = hasApiKey ? t('login.inputNewApiKey') : t('login.inputApiKey');
  const apiKeyPlaceholder = hasApiKey ? t('login.inputNewApiKey') : t('login.inputApiKey');

  const handleSubmit = async (values) => {
    const apiKey = values.title;

    if (!apiKey) {
      return message.error(t('login.errorApiKey'));
    }

    // 验证Key是否有效
    try {
      const response = await fetch('https://api.openai.com/v1/engines/text-davinci-003/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          prompt: '测试 API Key...',
          max_tokens: 5,
        }),
      });

      if (response.status === 200) {
        message.success(t('login.validateKey'));
        // Save the validated API key in the KeyStore
        console.log(apiKey);
        keyStore.setApiKey(apiKey);
        
        console.log(keyStore.apiKey);
      } else if (response.status === 401) {
        message.error(t('login.invalidApiKey'));
      } else {
        message.error(t('login.errorVerifyApiKey'));
      }
    } catch (error) {
      message.error(t('login.errorNetwork'));
    }
  };

  return (
    <Card
      title= {t('home.title')}
    >
      <Form form={form} initialValues={{ title: '' }} onFinish={handleSubmit}>
        <Form.Item
          label={apiKeyLabel}
          name="title"
          rules={[{ required: true, message: t('login.errorApiKey') }]}
        >
          <Input placeholder={apiKeyPlaceholder} style={{ width: '100%' }} />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            {t('login.validateKey')}
          </Button>
        </Form.Item>
      </Form>
      <div className="home-logo" />
      <div  
         
      />
      <div>
        {t('home.thanksMessage')}<br />
        {t('home.author')}<br />
        {t('home.email')}
      </div>
      
    </Card>
  );
};

export default Home;
