import './index.scss'
import { Button, Card, Checkbox, Form, Input, message, Modal } from 'antd';
import { useStore } from '@/store';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

function Login() {
  const [form] = Form.useForm();
  //const {loginStore} = useStore()
  const { keyStore } = useStore();
  const navigate = useNavigate()
  
  const hasApiKey = !!keyStore.apiKey;
  const apiKeyLabel = hasApiKey ? "新OpenAI key" : "OpenAI Key";
  const apiKeyPlaceholder = hasApiKey ? "请输入新的openAI key" : "请输入你的openAI key";

  const handleSubmit = async (values) => {
    const apiKey = values.title;

    if (!apiKey) {
      return message.error('请输入你的openAI key');
    }

    // 验证Key是否有效
    try {
      const response = await fetch('https://api.openai.com/v1/engines/text-davinci-003/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          prompt: '测试 API Key...',
          max_tokens: 5,
        }),
      });

      if (response.status === 200) {
        navigate('/')
        message.success('API Key 验证成功！');
        // Save the validated API key in the KeyStore
        console.log(apiKey);
        keyStore.setApiKey(apiKey);
        
        console.log(keyStore.apiKey);
      } else if (response.status === 401) {
        message.error('API Key 无效，请重试。');
      } else {
        message.error('验证 API Key 时发生错误，请稍后再试。');
      }
    } catch (error) {
      message.error('发生错误，请检查网络连接并稍后再试。');
    }
  };

  // Add these lines for the OpenAI Key explanation modal
  const [isModalOpen, setIsModalOpen] = useState(false);
  const showModal = () => {
    setIsModalOpen(true);
  };
  const handleOk = () => {
    setIsModalOpen(false);
  };
  const handleCancel = () => {
    setIsModalOpen(false);
  };
  return (
    <div className="login">
      <Card className="login-container">
      <Form form={form} initialValues={{ title: '' }} onFinish={handleSubmit}>
        <Form.Item>
          <div className = "company_name"/>
        </Form.Item>
          
            
          
        <Form.Item
          label={apiKeyLabel}
          name="title"
          rules={[{ required: true, message: '请输入你的openAI key' }]}
        >
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Input className="key_input" placeholder={apiKeyPlaceholder} style={{ flexGrow: 1 }} />
              <Button className="what_key_link" type="link" onClick={showModal} style={{ marginLeft: '8px' }}>
                Key？ 啥玩意？
              </Button>
            </div>
        </Form.Item>
        <Form.Item>
          
        </Form.Item>
        <Form.Item>
          <Button className = "verify_key" type="primary" htmlType="submit" >
            验证Key
          </Button>
        </Form.Item>
      </Form>
      </Card>
    </div>
  )
}

export default Login
