import { makeAutoObservable } from 'mobx';
import { history, setToken, getToken, removeToken } from '@/utils';
import * as openai from 'openai'; // 导入openai库
import { tokenCounter } from '../utils';
class ApiStore {
  apiKey = getToken() || '';
  selectedModel = localStorage.getItem('selectedModel') || 'gpt-3.5-turbo';

  constructor() {
    makeAutoObservable(this);
  }

  setApiKey = (apiKey) => {
    console.log(apiKey);
    this.apiKey = apiKey;
    setToken(apiKey);
  };

  getApiKey = () => {
    return this.apiKey;
  };

  removeApiKey = () => {
    this.apiKey = '';
    removeToken();
    history.push('/login');
  };

  setSelectedModel = (model) => {
    this.selectedModel = model;
    localStorage.setItem('selectedModel', model);
  };

  getSelectedModel = () => {
    return this.selectedModel;
  };



  callEngines = async (prompt = "test", model = "text-davinci-003") => {
    let maxTokens = prompt !== "test" ? 3700 - tokenCounter(prompt) : 1;
    try {
      const response = await fetch(
        `https://api.openai.com/v1/engines/${model}/completions`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${this.apiKey}`,
          },
          body: JSON.stringify({
            prompt: prompt,
            max_tokens: 5,
            stream: true,
          }),
        }
      );
  
      if (response.status === 200) {
        const reader = response.body.getReader();
        let result = "";
  
        
  
        const finalResult = await processStream();
  
        // Return the test case result or the actual completion content
        return prompt === "test" ? true : finalResult;
      } else {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error(error);
      return false;
    }
  };
  
  processStream = async () => {
    const { done, value } = await reader.read();

    if (done) {
      // Stream has ended; return the result
      return result;
    }

    const text = new TextDecoder("utf-8").decode(value);
    const lines = text?.split("\n").filter((line) => line.trim() !== "");

    for (const line of lines) {
      const message = line.replace(/^data: /, "");
      if (message === "[DONE]") {
        await reader.cancel(); // Stop reading the stream
        break; // Stream finished
      }
      try {
        const parsed = JSON.parse(message);
        result += parsed.choices[0].text; // Append the choice text to the result
      } catch (error) {
        console.error("Could not JSON parse stream message", message, error);
      }
    }

    return processStream(); // Continue processing the stream
  };

  callGPT = async (prompt = 'test', model = "gpt-4") => {
    try {
      let maxTokens;
      if (prompt === 'test') {
        maxTokens = 5;
      } else if (model === "gpt-4") {
        maxTokens = 7700 - tokenCounter(prompt);
      } else {
        maxTokens = 3700 - tokenCounter(prompt);
      }
  
      const response = await fetch(`https://api.openai.com/v1/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: model,
          messages: [{ role: "user", content: prompt }],
          max_tokens: maxTokens,
          temperature: 0.5,
          stream: true
        }),
      });
      console.log(response);
      if (response.status === 200) {
        const responseData = await response.json();
        console.log(responseData);
        return prompt === 'test' ? true : responseData.choices[0].message.content;
      } else {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error(error);
      return false;
    }
  };
  

  callModel = async (prompt , model ) => {
    if (model === 'gpt-3.5-turbo' || model === 'gpt-4') {
      return await this.callGPT(prompt, model);
    } else {
      return await this.callEngines(prompt, model);
    }
  };

  
}

export default ApiStore;
